package ejemplos;

public class relacionales {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int m=2, n=5;
		boolean res;
		res =m > n;//res=false
		System.out.println("es m mayor que n?:" +res);
		res =m < n;//res=true
		System.out.println("es m menor que n?:" +res);
		res =m >= n;//res=false
		System.out.println("es m mayor o igual que n?:" +res);
		res =m <= n;//res=true
		System.out.println("es m menor o igual que n?:" +res);
		res =m == n;//res=false
		System.out.println("es m igual que n?:" +res);
		res =m != n;//res=true
		System.out.println("es m distinto que n?:" +res);
		
		
		
		
		
	}
	
	

}
