package ejemplos;

public class asignaciones {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=5, b=2;
		a+=b;//7
		System.out.println(" a pasa a valer: "+a);
		a-=b;//5
		System.out.println(" a pasa a valer: "+a);
		a*=b;//10
		System.out.println(" a pasa a valer: "+a);
		a/=b;//5
		System.out.println(" a pasa a valer: "+a);
		a%=b;//1
		System.out.println(" a pasa a valer: "+a);
	}

}
